   export function useKeyboardAware() {
     const [isKeyboardVisible, setIsKeyboardVisible] = useState(false)

     useEffect(() => {
       const handleResize = () => {
         // ëª¨ë°”ì¼ì—ì„œ í‚¤ë³´ë“œê°€ ì˜¬ë¼ì˜¤ë©´ viewport ë†’ì´ê°€ ì¤„ì–´ë“¦
