   // hooks/useMediaQuery.ts
   import { useState, useEffect } from 'react'

   export function useMediaQuery(query: string) {
     const [matches, setMatches] = useState(false)

     useEffect(() => {
       const media = window.matchMedia(query)
       setMatches(media.matches)

