// In src/hooks/useReportStatus.js
export const useReportStatus = (sessionId) => {
  const [status, setStatus] = useState({
    hasExistingReport: false,
    isGenerating: false,
    isComplete: false,
    progress: 0
  });

  useEffect(() => {
    // Check if report exists
    const checkReportStatus = async () => {
      try {
        const response = await fetch(`/api/sessions/${sessionId}/report/status`);
        const data = await response.json();

        setStatus({
          hasExistingReport: data.exists,
          isGenerating: data.isGenerating,
          isComplete: data.isComplete,
          progress: data.progress || 0
        });

        // If report is generating but not complete, poll for updates
        if (data.isGenerating && !data.isComplete) {
          setTimeout(checkReportStatus, 2000);
        }
      } catch (error) {
        console.error('Failed to check report status:', error);
      }
    };

    checkReportStatus();
  }, [sessionId]);

  return status;
};
```

