// In src/hooks/useReportStatus.ts
import { useState, useEffect } from 'react';
import { fetchReportStatus } from '@/services/reportService';

export const useReportStatus = (sessionId) => {
  const [status, setStatus] = useState({
    hasExistingReports: false,
    isGenerating: false,
    reportTypes: {
      why: { exists: false, isGenerating: false },
      what: { exists: false, isGenerating: false },
      how: { exists: false, isGenerating: false }
    }
  });

  useEffect(() => {
    const checkStatus = async () => {
      const reportStatus = await fetchReportStatus(sessionId);
      setStatus(reportStatus);
    };

    checkStatus();

    // Poll for updates if reports are generating
    if (status.isGenerating) {
      const interval = setInterval(checkStatus, 3000);
      return () => clearInterval(interval);
    }
  }, [sessionId, status.isGenerating]);

  return status;
};
```

