// In src/app/api/session/[id]/report/route.ts
export async function GET(
  request: Request,
  { params }: { params: { id: string } }
) {
  const { searchParams } = new URL(request.url);
  const type = searchParams.get('type') || 'why';
  const sessionId = params.id;

  // Validate session exists
  const session = await db.session.findUnique({
    where: { id: sessionId },
    include: { user: true }
  });

  if (!session) {
    return new Response(JSON.stringify({ error: 'Session not found' }), {
      status: 404,
    });
  }

  // Check if report already exists
  const existingReport = await db.report.findFirst({
    where: {
      sessionId,
      type,
    },
  });

  if (existingReport) {
    return new Response(JSON.stringify(existingReport), {
      status: 200,
    });
  }

  // Handle different report types
  if (type === 'prologue') {
    return handlePrologueReport(session);
  } else if (type === 'why') {
    return handleWhyReport(session);
  } else {
    return new Response(JSON.stringify({ error: 'Invalid report type' }), {
      status: 400,
    });
  }
}

// Add new function to handle prologue report generation
async function handlePrologueReport(session) {
  try {
    // Get transcript data
    const messages = await db.message.findMany({
      where: { sessionId: session.id },
      orderBy: { createdAt: 'asc' },
    });

    // Extract transcript text
    const transcript = messages.map(msg => ({
      role: msg.role,
      content: msg.content
    }));

    // Get WHY_REFINED data if it exists
    const whyReport = await db.report.findFirst({
      where: {
        sessionId: session.id,
        type: 'why',
      },
    });

    const whyRefined = whyReport?.content?.WHY_REFINED || null;

    // Construct prologue prompt with required fields
    const prologuePrompt = {
      USER_NAME: session.user.name || 'User',
      transcript,
      WHY_REFINED: whyRefined,
      BAN_TEMPLATES: true
    };

    // Call AI service to generate prologue report
    const aiService = new AIService();
    const prologueResponse = await aiService.generatePrologueReport(prologuePrompt);

    // Store the report in the database
    const report = await db.report.create({
      data: {
        sessionId: session.id,
        type: 'prologue',
        content: prologueResponse,
      },
    });

    return new Response(JSON.stringify(report), {
      status: 200,
    });
  } catch (error) {
    console.error('Error generating prologue report:', error);
    return new Response(JSON.stringify({ error: 'Failed to generate prologue report' }), {
      status: 500,
    });
  }
}
