import Container from '@/components/layout/Container';
import Section from '@/components/layout/Section';
import Button from '@/components/ui/Button';
import { CopyToClipboard } from '@/components/ui/CopyToClipboard';

export default function ProloguePage() {
  const { id } = useParams();
  const [prologueData, setPrologueData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [oneLine, setOneLine] = useState('');
  const [saving, setSaving] = useState(false);
  const [saved, setSaved] = useState(false);

  // Fetch prologue data
  useEffect(() => {
    async function fetchPrologueData() {
      try {
        setLoading(true);
        const response = await fetch(`/api/session/${id}/report?type=prologue`);
        if (!response.ok) {
          throw new Error('Failed to fetch prologue data');
        }
        const data = await response.json();
        setPrologueData(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    }

    fetchPrologueData();
  }, [id]);

  // Handle one-line input submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!oneLine.trim()) return;

    try {
      setSaving(true);
      const response = await fetch(`/api/session/${id}/prologue`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ one_line: oneLine }),
      });

      if (!response.ok) {
        throw new Error('Failed to save prologue data');
      }

      setSaved(true);
      setTimeout(() => setSaved(false), 3000);
    } catch (err) {
      setError(err.message);
    } finally {
      setSaving(false);
    }
  };

  if (loading) {
    return (
      <Container>
        <Section>
          <div className="loading-indicator">Loading prologue data...</div>
        </Section>
      </Container>
    );
  }

  if (error) {
    return (
      <Container>
        <Section>
          <div className="error-message">Error: {error}</div>
        </Section>
      </Container>
    );
  }

  return (
    <Container>
      <Section>
        <h1>Prologue</h1>

        {/* Why ON/OFF Switch Section */}
        <div className="prologue-section">
          <h2>Why ON/OFF Switch</h2>
          <p>{prologueData.why_switch_explanation}</p>
          <div className="switch-container">
            {/* Toggle switch implementation */}
            <label className="switch">
              <input type="checkbox" />
              <span className="slider round"></span>
            </label>
            <span>Toggle ON/OFF</span>
          </div>
        </div>

        {/* Alternatives Section */}
        <div className="prologue-section">
          <h2>Alternatives</h2>
          <ul>
            {prologueData.alternatives.map((alt, index) => (
              <li key={index}>{alt}</li>
            ))}
          </ul>
        </div>

        {/* Narrative Section */}
        <div className="prologue-section">
          <h2>Narrative</h2>
          <p>{prologueData.narrative}</p>
          <CopyToClipboard text={prologueData.narrative} />
        </div>

        {/* Reflection Questions */}
        <div className="prologue-section">
          <h2>Reflection Questions</h2>
          <ol>
            {prologueData.reflection_questions.slice(0, 3).map((question, index) => (
              <li key={index}>{question}</li>
            ))}
          </ol>
        </div>

        {/* One-line Input */}
        <div className="prologue-section">
          <h2>Your One-Line Summary</h2>
          <form onSubmit={handleSubmit}>
            <div className="input-group">
              <input
                type="text"
                value={oneLine}
                onChange={(e) => setOneLine(e.target.value)}
                placeholder="Enter your one-line summary..."
                className="one-line-input"
                aria-label="One-line summary"
              />
              <Button 
                type="submit" 
                disabled={saving || !oneLine.trim()}
                aria-busy={saving}
              >
                {saving ? 'Saving...' : 'Enter'}
              </Button>
            </div>
          </form>
          {saved && <p className="success-message">Successfully saved!</p>}
        </div>
      </Section>
    </Container>
  );
}
```

