```tsx
// This would be implemented in a separate task, but the endpoint would be:
// src/app/api/session/[id]/prologue/route.ts
import { NextResponse } from 'next/server';
import { db } from '@/lib/db';

export async function POST(
  request: Request,
  { params }: { params: { id: string } }
) {
  try {
    const { one_line } = await request.json();
    const sessionId = params.id;

    // Save the one-line summary to the database
    await db.prologueData.upsert({
      where: { sessionId },
      update: { one_line },
      create: { sessionId, one_line },
    });

    return NextResponse.json({ success: true });
  } catch (error) {
    console.error('Error saving prologue data:', error);
    return NextResponse.json(
      { error: 'Failed to save prologue data' },
      { status: 500 }
    );
  }
}
```

