```typescript
// In src/utils/gateLogic.ts or relevant file
export const canAccessWhyPage = async (sessionId: string) => {
  const session = await db.session.findUnique({
    where: { id: sessionId },
    select: { 
      hasGeneratedReport: true,
      prologueCompleted: true 
    }
  });

  return session?.hasGeneratedReport && session?.prologueCompleted;
};

// Update middleware or route handlers to use this gate logic
// In src/middleware.ts or relevant route protection file
import { canAccessWhyPage } from '@/utils/gateLogic';

// Example middleware implementation
export async function middleware(request: NextRequest) {
  const pathname = request.nextUrl.pathname;

  // Check if trying to access why page
  if (pathname.match(/^\/session\/[^\/]+\/why$/)) {
    const sessionId = pathname.split('/')[2];
    const canAccess = await canAccessWhyPage(sessionId);

    if (!canAccess) {
      // Redirect to appropriate page based on state
      const session = await db.session.findUnique({
        where: { id: sessionId },
        select: { hasGeneratedReport: true }
      });

      if (session?.hasGeneratedReport) {
        // Has report but hasn't completed prologue
        return NextResponse.redirect(new URL(`/session/${sessionId}/prologue`, request.url));
      } else {
        // Hasn't generated report yet
        return NextResponse.redirect(new URL(`/session/${sessionId}/report`, request.url));
      }
    }
  }

  return NextResponse.next();
}
```

