       cy.get('[data-testid="continue-to-why"]').click();

       // Verify we're on the why page
       cy.url().should('include', '/session/test-session-id/why');
     });

     it('should block access to why page if prologue not completed', () => {
       // Set up session with report but no prologue
       cy.setupSessionWithReport();

       // Try to access why page directly
       cy.visit('/session/test-session-id/why');

       // Should be redirected to prologue
       cy.url().should('include', '/session/test-session-id/prologue');
     });
